package com.xy.nebulaol.vo;import com.xy.nebulaol.user.dto.resp.UserDetailsRespDto;import com.xy.nebulaol.user.po.NebulaolUserPo;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.Collection;public class SysUserVo implements UserDetails {    private Long id;    private String username;    private String password;    private boolean isEnabled;    private String clientId;    public SysUserVo(UserDetailsRespDto userDetailsRespDto) {        this.isEnabled = userDetailsRespDto.getIsEnabled() != null && userDetailsRespDto.getIsEnabled().equals(true);        this.id = userDetailsRespDto.getId();        this.username = userDetailsRespDto.getUsername();        this.password = userDetailsRespDto.getPassword();        this.clientId = userDetailsRespDto.getClientId();    }    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return null;    }    @Override    public String getPassword() {        return this.password;    }    @Override    public String getUsername() {        return this.username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return false;    }    public Long getId() {        return id;    }    public String getClientId() {        return clientId;    }    public void setClientId(String clientId) {        this.clientId = clientId;    }    public void setId(Long id) {        this.id = id;    }    public void setUsername(String username) {        this.username = username;    }    public void setPassword(String password) {        this.password = password;    }    public void setEnabled(Boolean enabled) {        isEnabled = enabled;    }}